import Keycloak from 'keycloak-js';

window.addEventListener("message", (event) => {
    console.log("ðŸ‘€ Message received from iframe:", event.data);
});

// Get the current URL as a string
const getCurrentUrl = () => {
    try {
        const url = new URL(window.location.href);
        // Remove any query parameters that might interfere with Keycloak
        url.search = '';
        return url.toString();
    } catch (error) {
        console.error('Failed to parse current URL:', error);
        // Fallback to using origin with path
        const origin = window.location.origin;
        const path = window.location.pathname;
        return `${origin}${path}`;
    }
};


// Ensure URL is a string
const ensureStringUrl = (url) => {
    if (typeof url === 'string') {
        return url;
    }
    if (url instanceof URL) {
        return url.toString();
    }
    if (typeof url === 'object' && url.origin) {
        return `${url.origin}${url.pathname || ''}`;
    }
    console.warn('Invalid URL format:', url);
    return window.location.origin;
};

const keycloakConfig = {
    url: 'http://localhost:9090',
    realm: 'whatsapp-clone',
    clientId: 'whatsapp-clone-app',
    redirectUri: window.location.origin + '/',
    onLoad: 'check-sso',
    silentCheckSsoRedirectUri: window.location.origin + '/Keycloak-silent-check-sso.html',
    pkceMethod: 'S256',
    checkLoginIframe: false,
    cors: true,
    corsOrigins: ['http://localhost:4200']
};

let keycloakInstance = null;
let initializationState = {
    initialized: false,
    inProgress: false,
    error: null
};

const handleInitError = (error, config) => {
    const errorDetails = {
        message: 'Unknown error during Keycloak initialization',
        details: 'No error object was provided',
        config: config
    };

    if (!error) {
        errorDetails.details = 'Initialization failed without error object';
        return errorDetails;
    }

    try {
        // Try to get error details from the error object
        errorDetails.message = error.message || error.toString() || 'Unknown error';
        errorDetails.details = error.stack || error.toString();
        errorDetails.code = error.code;
    } catch (e) {
        errorDetails.details = 'Error object could not be processed';
    }

    // Add configuration details
    errorDetails.config = {
        url: ensureStringUrl(config.url),
        realm: config.realm,
        clientId: config.clientId,
        redirectUri: ensureStringUrl(config.redirectUri),
        cors: config.cors,
        corsOrigins: config.corsOrigins
    };

    return errorDetails;
};

const checkKeycloakServer = async () => {
    try {
        // Use the well-known configuration endpoint with proper CORS handling
        const url = `${keycloakConfig.url}/realms/${keycloakConfig.realm}/.well-known/openid-configuration`;
        console.log('Checking Keycloak server at:', url);

        // Use no-cors mode to avoid CORS errors during the check
        const response = await fetch(url, {
            method: 'HEAD',
            mode: 'no-cors',
            cache: 'no-cache',
        });

        // If we get here, the server is accessible (no-cors always returns opaque response)
        console.log('Keycloak server check successful');
        return true;
    } catch (error) {
        console.error('Error checking Keycloak server accessibility:', error);
        return false;
    }
};

const initKeycloak = async () => {
    if (initializationState.inProgress) {
        throw new Error('Keycloak initialization already in progress');
    }

    if (initializationState.initialized) {
        return keycloakInstance;
    }

    initializationState.inProgress = true;

    try {
        console.log('Initializing Keycloak with config:', keycloakConfig);

        // Check if Keycloak server is accessible before initializing
        const serverAccessible = await checkKeycloakServer();
        if (!serverAccessible) {
            const error = new Error('Keycloak server is not accessible');
            error.serverAccessible = false;
            error.suggestions = [
                'Make sure Keycloak is running on port 9090',
                'Check that CORS is enabled in Keycloak for client whatsapp-clone-app',
                'Verify that http://localhost:4200 is added as a valid redirect URI in Keycloak client settings',
                'Ensure Web Origins in client settings includes http://localhost:4200'
            ];
            throw error;
        }

        // Initialize Keycloak with a promise wrapper for better error handling
        return new Promise((resolve, reject) => {
            keycloakInstance = new Keycloak(keycloakConfig);

            // Add event listeners for debugging
            keycloakInstance.onAuthSuccess = () => {
                console.log('Keycloak: Authentication successful');
            };

            keycloakInstance.onAuthError = (errorData) => {
                console.error('Keycloak: Authentication error:', errorData);
            };

            keycloakInstance.onTokenExpired = () => {
                console.log('Keycloak: Token expired');
            };

            keycloakInstance.init({
                onLoad: keycloakConfig.onLoad,
                silentCheckSsoRedirectUri: keycloakConfig.silentCheckSsoRedirectUri,
                pkceMethod: keycloakConfig.pkceMethod,
                checkLoginIframe: keycloakConfig.checkLoginIframe,
                enableLogging: true // Enable Keycloak's internal logging
            })
            .then(authenticated => {
                console.log('Keycloak initialized successfully. User authenticated:', authenticated);
                initializationState.initialized = true;
                initializationState.error = null;
                resolve(authenticated);
            })
            .catch(error => {
                console.error('Keycloak initialization error:', error);
                initializationState.error = error;
                reject(error);
            });
        });
    } catch (error) {
        console.error('Error during Keycloak initialization:', error);
        initializationState.error = error;
        throw error;
    } finally {
        initializationState.inProgress = false;
    }
};

const getKeycloak = () => {
    if (!initializationState.initialized) {
        throw new Error('Keycloak has not been initialized yet');
    }
    return keycloakInstance;
};

const getInitializationState = () => initializationState;

export { initKeycloak, getKeycloak, getInitializationState };
